interface FontObject {
  fontFamily?: string;
  fontWeight?:
    | 'normal'
    | 'bold'
    | '100'
    | '200'
    | '300'
    | '400'
    | '500'
    | '600'
    | '700'
    | '800'
    | '900'
    | undefined;
}

export interface FontTypes {
  thin: FontObject;
  light: FontObject;
  regular: FontObject;
  medium: FontObject;
  black: FontObject;
  bold: FontObject;
}

const fonts: FontTypes = {
  regular: {
    fontWeight: 'normal',
  },
  thin: {
    fontWeight: 'normal',
  },
  light: {
    fontWeight: 'normal',
  },
  black: {
    fontWeight: 'bold',
  },
  medium: {
    fontWeight: 'normal',
  },
  bold: {
    fontWeight: 'bold',
  },
};

export default fonts;
